import React from "react";
import {getTestimonial} from "../../../redux/action/admin/testimonial/action"
import { connect } from "react-redux"
class TestimonialList extends React.Component {


    render() {
        return(

            <div>
                <h3>Manage Testimonials</h3>
                <div className="table-responsive" >
                    {
                        (this.props.testimonial.length > 0) ? (
                            <table className="table " id="booking_tbl">
                                <thead>
                                    <tr>
                                    <th style={{width:'5%'}} >#</th>
                                    <th  style={{width:'15%'}}>Name</th>
                                    <th  style={{width:'15%'}}>Email</th>
                                    <th  style={{width:'40%'}}>Testimonials</th>
                                    <th  style={{width:'10%'}}>Posting Date</th>
                                    <th   style={{width:'15%'}}>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {

                                        this.props.testimonial.map( (el,index) => (
                                            <tr key={el.id}>
                                                <td>{index+1}</td>
                                                <td>{el.FullName}</td>
                                                <td>{el.VehiclesTitle}</td>
                                                <td>{el.FromDate}</td>
                                                <td>{el.ToDate}</td>
                                                <td >{el.message}</td>
                                                <td>{

                                                 (() => {

                                                     console.log("status",el)

                                                     if(el.Status == 0) {
                                                        return <span className="text-info">Not Confirmed yet</span>
                                                     }else if(el.Status == 1) {
                                                        return <span className="text-success">Confirmed</span>
                                                     }else if(el.Status == 2) {
                                                        return <span className="text-danger">Cancelled</span>
                                                     }
                                                 })()
                                                }</td>

                                                <td>{el.PostingDate}</td>
                                                <td>
                                                    <a className="ml-2" href="#" onClick={
                                                        () => this.confirmBooking(el.id)
                                                        }>Confirm</a>

                                                    <a className="ml-2" href="#" onClick={
                                                        () => this.cancelBooking(el.id)
                                                        }>Cancel</a>
                                                </td>
                                            </tr>
                                        ))
                                    }


                                </tbody>
                            </table>

                        ) : (
                            <h3>Loading</h3>
                        )
                    }

                </div>
            </div>
        )
    }

}

const mapStateToProps = () => {

}

const mapDispatchToProps = () => {

}

export default connect(mapStateToProps,mapDispatchToProps)(TestimonialList);
