import { GET_TESTIMONIAL } from "../../../action/admin/testimonial/actiontype";


const initialState = {
    testimonials : []
};

export const testimonialReducer = (state = initialState,action) => {

    if(action.type == GET_TESTIMONIAL){

        return  Object.assign({},state,{testimonials : action.payload})

    }else{
        return state;
    }
}
