import React from "react";
import { connect } from "react-redux";
import { getContacts,contactStatusUpdate } from "../../../redux/action/admin/contact/action";


class ContactUs extends React.Component {

    constructor(props) {
        super(props);
    }

    componentDidMount() {

    }

    render() {
        return(
            <div>

              <h3>Manage Testimonials</h3>
                <div className="table-responsive" >
                    {
                        (this.props.contacts.length > 0) ? (
                            <table className="table " id="booking_tbl">
                                <thead>
                                    <tr>
                                    <th style={{width:'5%'}} >#</th>
                                    <th  style={{width:'15%'}}>Name</th>
                                    <th  style={{width:'15%'}}>Email</th>
                                    <th  style={{width:'40%'}}>Testimonials</th>
                                    <th  style={{width:'10%'}}>Posting Date</th>
                                    <th   style={{width:'15%'}}>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {

                                        this.props.contacts.map( (el,index) => (
                                            <tr key={el.id}>
                                                <td>{index+1}</td>
                                                <td>{el.FullName}</td>
                                                <td>{el.UserEmail}</td>
                                                <td>{el.Testimonial}</td>
                                                <td>{el.PostingDate}</td>

                                                <td>
                                                    {
                                                        (el.status == 0) ? (
                                                            <a className="ml-2 text-danger" href="#" onClick={
                                                            () => this.activate(el.id)
                                                            }>In Active</a>
                                                        ) : (
                                                            <a className="ml-2 text-success" href="#" onClick={
                                                            () => this.inactivate(el.id)
                                                            }>Active</a>
                                                        )
                                                    }

                                                </td>
                                            </tr>
                                        ))
                                    }


                                </tbody>
                            </table>

                        ) : (
                            <h3>Loading</h3>
                        )
                    }

                </div>

            </div>
        )
    }

}

const mapStateToProps = (state) => {
    const contacts = state.contact;
    return {
        contacts : contacts.contact_list
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        getContacts : () => dispatch(getContacts()),
        contactStatusUpdate : (id) => dispatch(contactStatusUpdate(id)),
    }
}


export default connect(mapStateToProps,mapDispatchToProps)(ContactUs);
